import { render } from "@testing-library/react";
import React, { useState, useEffect, Component } from "react";

class MainAccComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {user: {}};
    }

    componentDidUpdate(prevProps) {
        if(this.props.user !== prevProps.user) this.setState({user: this.props.user});
        
        //console.log('this.state.user:', JSON.stringify(this.state.user, null, 3));
    }

    render(){
        if(this.state.user !== null){
            return(
                <div className="container-fluid">
                    <form>
                        <div>
                          <label id="accountusernameLabel" htmlFor="accountusername">Username</label>
                          <br/>
                          <p id="accountusername">{this.state.user.userName}</p>
                        </div>
                        <div>
                          <label id="accountpasswordLabel" htmlFor="accountpassword">Password</label>
                          <br/>
                          <p id="accountpassword">{this.state.user.password}</p>
                        </div>
                        <div>
                          <label id="bookholdLabel" htmlFor="bookhold">Book on Hold</label>
                          <br/>
                          <p id="bookhold">{this.state.user.bookOnHold}</p>
                        </div>
                    </form>
                </div>
            );
        }
        else{
            return(
                <div className="container-fluid">
                    <form>
                        <div>
                          <label id="accountusernameLabel" htmlFor="accountusername">Username</label>
                          <br/>
                          <p id="accountusername"></p>
                        </div>
                        <div>
                          <label id="accountpasswordLabel" htmlFor="accountpassword">Password</label>
                          <br/>
                          <p id="accountpassword"></p>
                        </div>
                        <div>
                          <label id="bookholdLabel" htmlFor="bookhold">Book on Hold</label>
                          <br/>
                          <p id="bookhold"></p>
                        </div>
                    </form>
                </div>
            );
        }
    }
}

export default MainAccComponent;
