import { Component } from "react";
import Form from 'react-validation/build/form';
import Input from 'react-validation/build/input';
import CheckButton from 'react-validation/build/button';
import { addBook } from "../../services/catalogService";

const required = value => {
    if(!value){
        return (
            <div className="alert alert-danger" role="alert">
                This field is required!
            </div>
        );
    }
};

class AddBookComponent extends Component {

    constructor(props){
        super(props);
        
        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeAuthor = this.onChangeAuthor.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        
        this.state = {
            title: "",
            author: "",
            publicationDate: null
        };
    }

    onChangeTitle(e) {
        this.setState({
            title: e.target.value
        });
    }

    onChangeAuthor(e) {
        this.setState({
            author: e.target.value
        });
    }

    onChangeDate(e) {
        this.setState({
            publicationDate: e.target.value
        });
    }

    handleAdd(e){
        e.preventDefault();

        //this.form.validateAll();

        if(this.state.author && this.state.title && this.state.publicationDate){
            //call api to add book
            addBook(this.state.title, this.state.author, this.state.date);
            
            this.setState({
                loading: false,
                message: "Added Book!"
            });
            //clear values
            
            //reload window
            window.location.reload();
        }
        else{
            this.setState({
                loading: false,
                message: "All values must be filled in"
            });
        }
    }

    render(){
        return(
            <div  className="col-md-12">
                <div className="card card-container">
                    <Form onSubmit={this.handleAdd} ref={c => {this.form = c;}}>
                        <h3>Add Book</h3>
                        <div className="form-group">
                            <label htmlFor="title">Title</label>
                            <Input type="text" className="form-control" name="title" value={this.state.title} onChange={this.onChangeTitle} validations={[required]} />
                        </div>
                        <div className="form-group">
                            <label htmlFor="author">Author</label>
                            <Input type="text" className="form-control" name="author" value={this.state.author} onChange={this.onChangeAuthor} validations={[required]} />
                        </div>
                        <div className="form-group">
                            <label htmlFor="pubdate">Publication Date</label>
                            <Input type="date" className="form-control" name="pubdate" value={this.state.publicationDate} onChange={this.onChangeDate} validations={[required]} />
                        </div>
                        <div className="form-group">
                            <button className="btn btn-primary">Add</button>
                        </div>
                    </Form>
                </div>
            </div>
        );
    }
}

export default AddBookComponent;
