import React, { Component } from 'react';
import { getCatalog } from '../../bodyPage/bodyservices/catalogService';

class MainCatComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            books: []
                /*{
                    "copyId": 1,
                    "title": {
                        "title": "Harry Potter and the Sorcerer's Stone",
                        "author": "J.K. Rowling",
                        "publicationDate": "2001-04-25",
                        "isbn": 1
                    },
                    "inStock": true,
                    "onHold": false,
                    "returnDate": null,
                    "holder": {
                        "first": 0,
                        "second": ""
                    },
                    "whoCheckedOut": ""
                },
                {
                    "copyId": 2,
                    "title": {
                        "title": "Harry Potter and the Sorcerer's Stone",
                        "author": "J.K. Rowling",
                        "publicationDate": "2001-04-25",
                        "isbn": 1
                    },
                    "inStock": true,
                    "onHold": false,
                    "returnDate": null,
                    "holder": {
                        "first": 0,
                        "second": ""
                    },
                    "whoCheckedOut": ""
                },
                {
                    "copyId": 3,
                    "title": {
                        "title": "Night Hoops",
                        "author": "Carl Deuker",
                        "publicationDate": "2000-08-20",
                        "isbn": 2
                    },
                    "inStock": true,
                    "onHold": false,
                    "returnDate": null,
                    "holder": {
                        "first": 0,
                        "second": ""
                    },
                    "whoCheckedOut": ""
                }*/
        };
        this.updateState = this.updateState.bind(this);
    }

    updateState(){
        getCatalog().then((response) => {
            this.setState({books: JSON.stringify(response.data, null, 3)});
        });

        console.log('books:', this.state.books);
    }

    render(){
        return(
            <div className="container-fluid">
                <table className ="table table-hover">
                    <thead>
                        <tr>
                            <th style={{'textAlign': 'center'}} colSpan={4}>Library Catalog</th>
                        </tr>
                        <tr>
                            <td>Book Id</td>
                            <td>Title</td>
                            <td>in Stock</td>
                            <td>on Hold</td>
                        </tr>
                    </thead>
                    { this.updateState() }
                </table>
            </div>
        );
    }
}

export default MainCatComponent;
