import { Component } from "react";
import Form from 'react-validation/build/form';
import Input from 'react-validation/build/input';
import { addBook } from "../../services/catalogService";

const required = value => {
    if(!value){
        return (
            <div className="alert alert-danger" role="alert">
                This field is required!
            </div>
        );
    }
};

export default class AddBookComponent extends Component {

    constructor(props){
        super(props);
        
        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeAuthor = this.onChangeAuthor.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        
        this.state = {
            title: "",
            author: "",
            publicationDate: null
        };
    }

    onChangeTitle(e) {
        this.setState({
            title: e.target.value
        });
    }

    onChangeAuthor(e) {
        this.setState({
            author: e.target.value
        });
    }

    onChangeDate(e) {
        this.setState({
            publicationDate: e.target.value
        });
    }

    handleAdd(e){
        //e.preventDefault();

        //this.form.validateAll();
        console.log("author: " + JSON.stringify(this.state.author,null,3));
        console.log("title: " + JSON.stringify(this.state.title,null,3));
        console.log("date: " + JSON.stringify(this.state.date,null,3));
    }

    render(){
        return(
            <div  className="col-md-12">
                <div className="card card-container">
                    <form>
                        <h3>Add Book</h3>
                        <div className="form-group">
                            <label htmlFor="title">Title</label>
                            <input type="text" className="form-control" name="title" value={this.state.title} onChange={this.onChangeTitle} />
                        </div>
                        <div className="form-group">
                            <label htmlFor="author">Author</label>
                            <input type="text" className="form-control" name="author" value={this.state.author} onChange={this.onChangeAuthor} />
                        </div>
                        <div className="form-group">
                            <label htmlFor="pubdate">Publication Date</label>
                            <input type="date" className="form-control" name="pubdate" value={this.state.publicationDate} onChange={this.onChangeDate} />
                        </div>
                        <div className="form-group">
                            <button onClick={this.handleAdd} type="button" className="btn btn-primary">Add</button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}
