import { Component } from 'react';
import AuthService from '../../services/authService';
import {checkIn, getCheckedOut} from '../../services/catalogService';


class CheckInComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {
            user: AuthService.getCurrentUser(),
            books: null
        };

        this.checkin = this.checkin.bind(this);
    }

    componentDidMount() {
        getCheckedOut().then((response) => {
            if(JSON.stringify(response.data, null, 3) !== "")
                this.setState({books: response.data});
            //console.log('response:', JSON.stringify(response.data, null, 3));
            
        });
    }

    checkin(id) {
        checkIn(id);
    }

    render(){
        if(this.state.books !== null){
            return(
                <div className="container-fluid">
                    <table className ="table table-hover table-striped">
                        <thead>
                            <tr>
                                <th style={{'textAlign': 'center'}} colSpan={4}>Checked Out Books</th>
                            </tr>
                            <tr>
                                <td>Title Id</td>
                                <td>Title</td>
                                <td>Author</td>
                            </tr>
                        </thead>
                        <tbody className="table-primary">
                            {this.state.books.map((item, index) => {
                                return(
                                    <tr key={"row" + index}>
                                        <td key={"book" + index}>{item.copyId}</td>
                                        <td key={"title" + index}>{item.title}</td>
                                        <td key={"author" + index}>{item.author}</td>
                                        <td><button className="btn btn-primary" type="submit" onClick={() => {
                                            this.checkin(item.copyId);
                                            window.location.reload();
                                        }}>Check In</button></td>
                                    </tr>
                                );
                            })} 
                        </tbody>
                    </table>
                </div>
            ); 
        }
        else if(this.state.books === null){
            return(
                <div className="container-fluid">
                    <table className ="table">
                        <thead>
                            <tr>
                                <th style={{'textAlign': 'center'}} colSpan={3}>Checked Out Books</th>
                            </tr>
                            <tr>
                                <td>Title Id</td>
                                <td>Title</td>
                                <td>Author</td>
                            </tr>
                        </thead>
                        <tbody>
                            <h3>No books checked out.</h3>
                        </tbody>
                    </table>
                </div>
            );
        }
    }
}

export default CheckInComponent;
