import React, { useState, useEffect } from 'react';
import { getCatalog } from '../../bodyPage/bodyservices/catalogService';

function MainCatComponent() {

    const[ books, setBooks ] = useState([]);

    /*getCatalog().then((response) => {
        //console.log('response:', JSON.stringify(response.data, null, 3));
        setBooks(JSON.stringify(response.data, null, 3));
    });*/

    useEffect(() => {
        console.log('books:', books);

        getCatalog().then((response) => {
            //console.log('response:', JSON.stringify(response.data, null, 3));
            setBooks(JSON.stringify(response.data, null, 3));
        });

     }, [books]);

    return(
        <div className="container-fluid">
            <table className ="table table-hover">
                <thead>
                    <tr>
                        <th style={{'textAlign': 'center'}} colSpan={4}>Library Catalog</th>
                    </tr>
                    <tr>
                        <td>Book Id</td>
                        <td>Title</td>
                        <td>in Stock</td>
                        <td>on Hold</td>
                    </tr>
                </thead>
            </table>
        </div>
    );
}   


export default MainCatComponent;
