import React, { Component } from 'react';
import { getCatalog } from '../../bodyPage/bodyservices/catalogService';

class MainCatComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {books: []};
        //this.componentWillMount = this.componentWillMount.bind(this);
    }

    UNSAFE_componentWillMount() {
        getCatalog().then((response) => {
            console.log('response:', JSON.stringify(response.data, null, 3));
            this.setState({books: JSON.stringify(response.data, null, 3)});
        });
    }
    
    render(){
        const { books } = this.state;
        console.log('books:', books);

        return(
            <div className="container-fluid">
                <table className ="table table-hover">
                    <thead>
                        <tr>
                            <th style={{'textAlign': 'center'}} colSpan={4}>Library Catalog</th>
                        </tr>
                        <tr>
                            <td>Book Id</td>
                            <td>Title</td>
                            <td>in Stock</td>
                            <td>on Hold</td>
                        </tr>
                    </thead>
                    <tbody>
                        {books.map((item, index) => {
                            return(
                                <tr key={"row" + index}>
                                    <td key={"book" + index}>{item.copyId}</td>
                                    <td key={"title" + index}>{item.title.title}</td>
                                    <td key={"instock" + index}>{JSON.stringify(item.inStock.valueOf())}</td>
                                    <td key={"onhold" + index}>{JSON.stringify(item.onHold.valueOf())}</td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            </div>
        );
    }
}   


export default MainCatComponent;
