import React, { Component } from 'react';

class MainCatComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            books: [{
                    "copyId": 1,
                    "title": {
                        "title": "Harry Potter and the Sorcerer's Stone",
                        "author": "J.K. Rowling",
                        "publicationDate": "2001-04-25",
                        "isbn": 1
                    },
                    "inStock": true,
                    "onHold": false,
                    "returnDate": null,
                    "holder": {
                        "first": 0,
                        "second": ""
                    },
                    "whoCheckedOut": ""
                }]
        };
    }

    render(){
        return(
            <div className="container-fluid">
                <table className ="table table-hover">
                    <thead>
                        <tr>
                            <th style={{'textAlign': 'center'}} colSpan={4}>Library Catalog</th>
                        </tr>
                        <tr>
                            <td>Book Id</td>
                            <td>Title</td>
                            <td>in Stock</td>
                            <td>on Hold</td>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.books.map((item, index) => {
                            return(
                                <tr key={"row" + index}>
                                    <td key={"book" + index}>{item.copyId}</td>
                                    <td key={"title" + index}>{item.title.title}</td>
                                    <td key={"instock" + index}>{item.inStock}</td>
                                    <td key={"onhold" + index}>{item.onHold}</td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default MainCatComponent;
