import axios from "axios";

const rootURL = "http://localhost:7071/copies/";

let testUrl = () => {
    try{
        let pobj = axios.get(rootURL);
        return pobj;
    }
    catch(exception){
        console.log(rootURL + ": not correct " + (exception));
    }
}

let getBookTotal = () => {
    try{
        let pobj = axios.get(rootURL + "totalBooks");

        pobj.then((response) => {
            console.log(JSON.stringify(response.data, null, 3));
            //return JSON.stringify(response.data, null, 3);
        });

        return JSON.stringify(pobj.then.response.data, null, 3);
    }
    catch(exception){
        console.log(rootURL + "totalBooks: not correct " + (exception));
    }
}

export {testUrl, getBookTotal};
