import React, { useCallback, useState, useEffect } from 'react';
import { createUser } from './headerservices/signupService';
import { logIn } from './headerservices/signinService';
import AuthService from '../components/services/authService';

function HeaderComponent(props){

  const [ user, setUser ] = useState(null);

  const logOut = useCallback(() => {
    AuthService.logout()
  }, []);

  //const [ flag, setFlag ] = useState(false);

 /* const signUp = useCallback(() => {

    let username = document.getElementById("signupusername").value;
    let pass = document.getElementById("signuppassword").value;
    let pass2 = document.getElementById("confirmPassword").value;


    if(pass === pass2){
      let pobj = createUser(username, pass);

      pobj.then((response) => {
        //console.log(JSON.stringify(response.data, null, 3));
      });

      pobj.catch((error) => {
        console.log(JSON.stringify(error, null, 3))
      });

      document.getElementById("suSuc").style.display = "block";
      document.getElementById("suFail").style.display = "none";
    }
    else{
      console.error("Passwords didn't match");

      document.getElementById("suFail").style.display = "block";
      document.getElementById("suSuc").style.display = "none";
    }
  
  }, []);*/

  /*const signIn = useCallback(() => {

    let username = document.getElementById("signinusername").value;
    let password = document.getElementById("signinpassword").value;

    let pobj = logIn(username, password);

    pobj.then((response) => {
      //console.log("response data: " + JSON.stringify(response.data, null, 3));
      if(response.data !== ""){ 
        setUser(response.data);
      }
    });

    setFlag(true);
  }, []);*/


  /*useEffect(() => {
      
    }
 }, [flag, props, user]);*/

  return (
      <div  className="container-fluid">
        <div className="row">
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="col-10">
              <a className="navbar-brand" href="/dashboard">Legacy Library</a>
            </div>
            <div className="col">
              <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
              </button>
          
              {user ? (
                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                  <li className="nav-item">
                    <a role="button" className="btn nav-link" href='/login' onClick={logOut}>LogOut</a>
                  </li>
                </ul>
              </div>
              ) : (
              <div className="collapse navbar-collapse" id="navbarSupportedContent">
                <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                  <li className="nav-item">
                    <a href="/register" role="button" className="btn nav-link">Sign up</a>
                  </li>
                  <li className="nav-item">
                    <a role="button" className="btn nav-link" href='/login'>Sign In</a>
                  </li>
                </ul>
              </div>)}
            </div>
          </nav>
        </div>
      </div>
  );
}

export default HeaderComponent;
