import { Component } from "react";
import AuthService from '../services/authService'

export default class LoginComponent extends Component {
    constructor(props){
        super(props);
        this.handleLogin = this.handleLogin.bind(this);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        
        this.state = {
            username: "",
            password: "",
            loading: false,
            message: ""
        };
    }

    onChangePassword(e) {
        this.setState({
            password: e.target.value
        });
    }

    onChangeUsername(e) {
        this.setState({
            username: e.target.value
        });
    }

    handleLogin(e){
        e.preventDefault();

        this.setState({
            message: "",
            loading: true
        });

        this.form.validateAll();

        if(this.checkBtn.context._errors.length === 0){
            AuthService.login(this.state.username, this.state.password).then(
                () => {
                    this.props.history.push("/dashboard");
                    window.location.reload();
                },
                error => {
                    const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.resposne.data.message) ||
                        error.message ||
                        error.toString();
                    
                    this.setState({
                        loading: false,
                        message: resMessage
                    });
                }
            );
        }
        else{
            this.setState({
                loading: false
            });
        }
    }

    render(){
        return (
            <div  className="col-md-12">
                <div className="card card-container">
                    <form onSubmit={this.handleLogin} ref={c => {this.form = c;}}>
                        <div className="form-group">
                            <label htmlFor="username">Username</label>
                            <input type="text" className="form-control" name="username" value={this.state.username} onChange={this.onChangeUsername} validations={[required]} />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input type="password" className="form-control" name="password" value={this.state.password} onChange={this.onChangePassword} validations={[required]} />
                        </div>
                        <div className="form-group">
                            <button>
                                {this.state.loading && (<span className="spinner-border spinner-border-sm"></span>)}
                                <span>Login</span>
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}
