import React, { useState, useEffect } from "react";

function MainAccComponent(props) {

    const [ user, setUser ] = useState(null);

    useEffect(() => {
        setUser(props.user);
        //console.log("body user: " + JSON.stringify(user, null, 3));
    }, [props.user]);

    return(
        <div className="container-fluid">
            <form>
                <div>
                  <label id="accountusernameLabel" htmlFor="accountusername">Username</label>
                  <br/>
                  <p id="accountusername">{user.userName}</p>
                </div>
                <div>
                  <label id="accountpasswordLabel" htmlFor="accountpassword">Password</label>
                  <br/>
                  <p id="accountpassword">{}</p>
                </div>
                <div>
                  <label id="bookholdLabel" htmlFor="bookhold">Book on Hold</label>
                  <br/>
                  <p id="bookhold">{}</p>
                </div>
            </form>
        </div>
    );
}

export default MainAccComponent;
