import React, { Component } from 'react';
import { getCatalog } from '../../bodyPage/bodyservices/catalogService';

class MainCatComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {books: []};
    }

    componentDidMount() {
        getCatalog().then((response) => {
            //console.log('response:', JSON.stringify(response.data, null, 3));
            this.setState({books: JSON.stringify(response.data, null, 3)});
        });
    }
    
    render(){
       if(this.state.books === null){
        return(
            <div className="container-fluid">
                <table className ="table table-hover">
                    <thead>
                        <tr>
                            <th style={{'textAlign': 'center'}} colSpan={4}>Library Catalog</th>
                        </tr>
                        <tr>
                            <td>Book Id</td>
                            <td>Title</td>
                            <td>in Stock</td>
                            <td>on Hold</td>
                        </tr>
                    </thead>
                </table>
            </div>
        ); 
       }
       else{
        console.log("books:", this.state.books.keys());
            return(
                <div className="container-fluid">
                    <table className ="table table-hover">
                        <thead>
                            <tr>
                                <th style={{'textAlign': 'center'}} colSpan={3}>Library Catalog</th>
                            </tr>
                            <tr>
                                <td>Book Id</td>
                                <td>Title</td>
                                <td>Author</td>
                            </tr>
                        </thead>
                        <tbody>
                            { //console.log("object keys books:", Object.keys(this.state.books))
                            /*Object.keys(this.state.books).map((item, index) => {
                                return(
                                    <tr key={"row" + index}>
                                        <td key={"book" + index}>{item.isbn}</td>
                                        <td key={"title" + index}>{item.title}</td>
                                        <td key={"author" + index}>{item.author}</td>
                                    </tr>
                                );
                            })*/}
                        </tbody>
                    </table>
                </div>
            );
        }
    }
}   


export default MainCatComponent;
