import { Component } from "react";
import AuthService from '../services/authService';
import Form from 'react-validation/build/form';
import Input from 'react-validation/build/input';
import CheckButton from 'react-validation/build/button';
import { withRouter } from "react-router-dom";

const required = value => {
    if(!value){
        return (
            <div className="alert alert-danger" role="alert">
                This field is required!
            </div>
        );
    }
};

export default class LoginComponent extends Component {
    constructor(props){
        super(props);
        this.handleLogin = this.handleLogin.bind(this);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        
        this.state = {
            username: "",
            password: "",
            loading: false,
            message: ""
        };
    }

    onChangePassword(e) {
        this.setState({
            password: e.target.value
        });
    }

    onChangeUsername(e) {
        this.setState({
            username: e.target.value
        });
    }

    handleLogin(e){
        e.preventDefault();

        this.setState({
            message: "",
            loading: true
        });

        this.form.validateAll();

        if(this.checkBtn.context._errors.length === 0){
            AuthService.login(this.state.username, this.state.password).then(
                () => {
                    //this.props.history.push("/account");
                    //this.context.history.push("/account");
                    window.location.reload();
                },
                error => {
                    const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.resposne.data.message) ||
                        error.message ||
                        error.toString();
                    
                    this.setState({
                        loading: false,
                        message: resMessage
                    });
                }
            );
        }
        else{
            this.setState({
                loading: false
            });
        }
    }

    render(){
        return (
            <div  className="col-md-12">
                <div className="card card-container">
                    <Form onSubmit={this.handleLogin} ref={c => {this.form = c;}}>
                        <div className="form-group">
                            <label htmlFor="username">Username</label>
                            <Input type="text" className="form-control" name="username" value={this.state.username} onChange={this.onChangeUsername} validations={[required]} />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <Input type="password" className="form-control" name="password" value={this.state.password} onChange={this.onChangePassword} validations={[required]} />
                        </div>
                        <div className="form-group">
                            <button className="btn btn-primary">
                                {this.state.loading && (<span className="spinner-border spinner-border-sm"></span>)}
                                <span>Login</span>
                            </button>
                        </div>

                        {this.state.message && (
                            <div>
                                <div className="alert alert-danger" role="alert">{this.state.message}</div>
                            </div>
                        )}
                        <CheckButton
                            style={{display: "none"}}
                            ref={c => {
                                this.checkBtn = c;
                            }}/>
                    </Form>
                </div>
            </div>
        );
    }
}
