import { Component } from "react";
import AuthService from '../services/authService'

export default class RegisterComponent extends Component {
    constructor(props){
        super(props);
        this.handleRegister = this.handleRegister.bind(this);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        
        this.state = {
            username: "",
            password: "",
            successful: false,
            message: ""
        };
    }
    
    onChangeUsername(e){
        this.setState({username: e.target.value});
    }

    onChangePassword(e){
        this.setState({password: e.target.value});
    }

    handleRegister(e){
        e.preventDefault();

        this.setState({
            message: "",
            successful: false
        });

        this.form.validateAll();

        if(this.checkBtn.context._errors.length === 0){
            AuthService.register(
                this.state.username,
                this.state.password
            ).then(
                response => {
                    this.setState({
                        message: response.data.message,
                        successful: true
                    });
                },
                error => {
                    const resMessage = 
                        (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                        error.message ||
                        error.toString();

                        this.setState({
                            successful: false,
                            message: resMessage
                        });
                }
            );
        }
    }

    render(){
        return (
            <div  className="col-md-12">
                <div className="card card-container">
                    <form onSubmit={this.handleRegister} ref={c => {this.form = c;}}>
                        {!this.state.successful && (
                            <div>
                                <div className="form-group">
                                    <label htmlFor="username">Username</label>
                                    <input type="text" className="form-control" name="username" value={this.state.username} onChange={this.onChangeUsername} required />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="password">Password</label>
                                    <input type="password" className="form-control" name="password" value={this.state.password} onChange={this.onChangePassword} required />
                                </div>
                        <       div className="form-group">
                                    <button className="btn btn-primary btn-block">Sign Up</button>
                                </div>
                            </div>)}

                        {this.state.message && (
                            <div>
                                <div className="alert alert-danger" role="alert">{this.state.message}</div>
                            </div>
                        )}

                    </form>
                </div>
            </div>
        );
    }

}
