import React, { useEffect, useState } from "react";
import { getBookTotal } from "../../bodyservices/cardService";

function CardComponent() {
    
    /*useCallback(() => {

        let pobj = getBookTotal();

        pobj.then((response) => {
            console.log(JSON.stringify(response.data, null, 3));
            document.getElementById("totalBooks").innerHTML.value = JSON.stringify(response.data, null, 3);
          });

    }, []);*/

    const[ totalBooks ] = useState(getBookTotal().then.response.data);

    useEffect(() => {
        console.log('Total: ', totalBooks);
     }, [totalBooks]);

    return(
        <div className="container-fluid">
            <div className="row">
                <div className="col">
                    <div className="card text-white bg-primary mb-3"  style={{width: "18rem"}}>
                         <div className="card-body">
                            <h5 className="card-title">Total Books</h5>
                            <p id="totalBooks" className="card-text">{ totalBooks }</p>
                        </div>
                    </div>
                </div>
                <div className="col">
                    <div className="card text-white bg-info mb-3"  style={{width: "18rem"}}>
                         <div className="card-body">
                            <h5 className="card-title">Total Available</h5>
                            <p className="card-text"></p>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row">
                <div className="col">
                    <div className="card text-white bg-success mb-3"  style={{width: "18rem"}}>
                        <div className="card-body">
                            <h5 className="card-title">Total Users</h5>
                            <p className="card-text"></p>
                        </div>
                    </div>
                </div>
                <div className="col">
                    <div className="card text-white bg-warning mb-3"  style={{width: "18rem"}}>
                        <div className="card-body">
                            <h5 className="card-title">Total Admin</h5>
                            <p className="card-text"></p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default  CardComponent;
