import React, { useState, useEffect, useCallback } from "react";

function SidebarComponent(props) {

    const [ user, setUser ] = useState(null);

    const [ flag, setFlag ] = useState(false);

    const [ accountNavs, setaccountNavs ] = useState([]);

    const setNavs = useCallback(() => {
        let link1 = document.createElement("a");
        let link2 = document.createElement("a");

        link1.className = "nav-link";
        link2.className = "nav-link";

        link1.href = "/account";
        link2.href = "/mybooks";

        link1.innerHTML = "Account";
        link2.innerHTML = "My Books";
        
        setaccountNavs([link1, link2]);
    }, []);

    useEffect(() => {
        setUser(props.user);

        if(flag === true){
            if(user !== null){
                setNavs();
                //console.log("user is not null");

                let link1 = document.createElement("a");
                let link2 = document.createElement("a");

                link1.className = "nav-link";
                link2.className = "nav-link";

                link1.href = "/account";
                link2.href = "/mybooks";

                link1.innerHTML = "Account";
                link2.innerHTML = "My Books";

                /*let item1 = document.createElement("li");
                let item2 = document.createElement("li");
                item1.appendChild(link1);
                item2.appendChild(link2);


                let list = document.getElementById("verticalNav");
                list.appendChild(item1);
                list.appendChild(item2);*/

                //let hiddenLi = list.innerHTML.split("</li>");
                //setaccountNavs(hiddenLi[2] + "</li>" + hiddenLi[3] + "</li>");
                
                
                setaccountNavs([link1, link2]);

                //console.log("nav: " + hiddenLi[2] + "</li>" + hiddenLi[3] + "</li>");
                //console.log("nav: " + [link1, link2]);
            }
            //setFlag(false);
        }
        setFlag(true);
        //console.log("sidebar user: " + JSON.stringify(user, null, 3));
    }, [accountNavs, flag, props.user, setNavs, user]);

    return(
        <div className="container-fluid">
            <nav className="nav flex-column">
                <ul className="nav nav-pills" id="verticalNav">
                    <li id="verticalNav-1">
                        <a className="nav-link" aria-current="page" href="/dashboard">Dashboard</a>
                    </li>
                    <li id="verticalNav-2">
                        <a className="nav-link" href="/catalog">Catalog</a>
                    </li>
                    {accountNavs.map((item, index) => {
                             return(
                                <li key={"row" + index}>
                                    <a className="nav-link" href="/" key={index}>{item.innerText}</a>
                                </li>
                            );
                    })}
                </ul>
            </nav>
        </div>
    );
}

export default SidebarComponent;
