import React, { useState, useEffect } from "react";
import { Component } from "react/cjs/react.production.min";

class MainMyBooksComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {user: null};
    }

    componentDidMount() {
        this.setState({user: this.props.user});
    }
    
    render(){
       if(this.state.user === null){
        return(
            <div className="container-fluid">
                <table className ="table table-hover">
                    <thead>
                        <tr>
                            <th style={{'textAlign': 'center'}} colSpan={3}>Library Catalog</th>
                        </tr>
                        <tr>
                            <td>Title Id</td>
                            <td>Title</td>
                            <td>Author</td>
                        </tr>
                    </thead>
                </table>
            </div>
        ); 
       }
       else{
            return(
                <div className="container-fluid">
                    <table className ="table table-hover">
                        <thead>
                            <tr>
                                <th style={{'textAlign': 'center'}} colSpan={3}>My Books</th>
                            </tr>
                            <tr>
                                <td>Title Id</td>
                                <td>Title</td>
                                <td>Author</td>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.user.books.map((item, index) => {
                                return(
                                    <tr key={"row" + index}>
                                        <td key={"book" + index}>{item.isbn}</td>
                                        <td key={"title" + index}>{item.title}</td>
                                        <td key={"author" + index}>{item.author}</td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
                </div>
            );
        }
    }
}

export default MainMyBooksComponent;
