import axios from "axios";

const rootCopyURL = "http://localhost:7071/copies/";
const rootUserURL = "http://localhost:7071/users/";

let testUrl = () => {
    try{
        let pobj = axios.get(rootURL);
        return pobj;
    }
    catch(exception){
        console.log(rootURL + ": not correct " + (exception));
    }
}

let getBookTotal = () => {
    try{
        let pobj = axios.get(rootCopyURL + "totalBooks");

        pobj.then((response) => {
            console.log(JSON.stringify(response.data, null, 3));
        });

        return pobj;
    }
    catch(exception){
        console.log(rootCopyURL + "totalBooks: not correct " + (exception));
    }
}

let getBooksAvail = () => {
    try{
        let pobj = axios.get(rootCopyURL + "totalAvailable");

        pobj.then((response) => {
            console.log(JSON.stringify(response.data, null, 3));
        });

        return pobj;
    }
    catch(exception){
        console.log(rootCopyURL + "totalBooks: not correct " + (exception));
    }
}

let getAllUsers = () => {
    try{
        let pobj = axios.get(rootUserURL + "viewAllUsers");

        pobj.then((response) => {
            console.log(JSON.stringify(response.data, null, 3));
        });

        return pobj;
    }
    catch(exception){
        console.log(rootUserURL + "totalBooks: not correct " + (exception));
    }
}

export {testUrl, getBookTotal, getBooksAvail, getAllUsers};
