import React, { useState, useEffect } from "react";

function SidebarComponent(props) {

    const [ user, setUser ] = useState(null);

    const [ flag, setFlag ] = useState(false);

    /*const settingFlag = useCallback((flag) => {
    
        let pobj = logIn(username, password);
    
        pobj.then((response) => {
          //console.log("response data: " + JSON.stringify(response.data, null, 3));
          if(response.data !== ""){ 
            setUser(response.data);
          }
        });
    
        setFlag(true);
      }, []);*/

    useEffect(() => {
        setUser(props.user);
        //settingFlag(true);

        if(flag === true){
            if(user !== null){
                console.log("user is not null");
                
                /*document.getElementById("verticalNav").innerHTML = document.getElementById("verticalNav").innerHTML.concat(
                    <li>
                        <a className="nav-link" href="/account">Account</a>
                    </li>
                );*/

                let link = document.createElement("a");
                link.className = "nav-link";
                link.href = "/account";

                let item = document.createElement("li");
                item.innerHTML = link.innerHTML;


                let list = document.getElementById("verticalNav");
                list.appendChild(item);

                console.log("nav: " + document.getElementById("verticalNav").innerHTML);
            }
            
            console.log("sidebar user: " + JSON.stringify(user, null, 3));
        }
            //console.log("user not null");
            //console.log("user: " + user);
       //}
        //else{
            //go through all the users
        //whichever user matces the username.->then check password
        //if correct -> log in
        //get information
        //display additonal tabs
        //use information in account & my books
        //****if admin -> diplay admin controls tab
            //console.log("body user: " + JSON.stringify(user, null, 3));
            //console.log("user is null");
        //}
        setFlag(true);
        //console.log("sidebar user: " + JSON.stringify(user, null, 3));
    }, [flag, props.user, user]);

    return(
        <div className="container-fluid">
            <nav className="nav flex-column">
                <ul className="nav nav-pills" id="verticalNav">
                    <li id="verticalNav-1">
                        <a className="nav-link" aria-current="page" href="/dashboard">Dashboard</a>
                    </li>
                    <li id="verticalNav-2">
                        <a className="nav-link" href="/catalog">Catalog</a>
                    </li>
                </ul>
            </nav>
        </div>
    );
}

export default SidebarComponent;
