import BodyComponent from '../components/bodyPage/bodyComponent';
import HeaderComponent from '../components/headerPage/headerComponent';
import CatalogComponent from '../components/bodyPage/catalogComponent';
import AccountComponent from '../components/bodyPage/accountComponent';
import MyBooksComponent from '../components/bodyPage/mybooksComponent';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useCallback, useState, useEffect } from 'react';
import AuthService from '../components/services/authService';
import './App.css';

function App() {

  const [ user, setUser ] = useState(null);

  /*const myCallback = useCallback((dataFromHeader) => {
    setUser(dataFromHeader);
  }, []);*/

  const logOut = useCallback(() => {AuthService.logout()});

  useEffect(() => {
    const user = AuthService.getCurrentUser();

    if(user){
      setUser(AuthService.getCurrentUser());
    }
  });

  return (
    <div className="App">
      <HeaderComponent /*callbackFromParent={myCallback}*//>

      <div className="container-fluid">
        <Router>
          <Routes>
            <Route path="/" exact element={<BodyComponent user={user}/>}/>
            <Route path="/dashboard" exact element={<BodyComponent user={user}/>}/>
            <Route path="/catalog" exact element={<CatalogComponent user={user}/>}/>
            <Route path="/account" exact element={<AccountComponent user={user}/>}/>
            <Route path="/mybooks" exact element={<MyBooksComponent user={user}/>}/>
          </Routes>
        </Router>
      </div>
    </div>
  );
}

export default App;
